import { exec } from 'child_process';
import { createHash } from 'crypto';
export const hash = (id, algo) => {
    const h = createHash(algo);
    h.update(id);
    return h.digest('hex');
};
export const execa = async (command, parse) => new Promise((resolve, reject) => {
    exec(command, (error, stdout) => {
        if (error) {
            reject(error);
            return;
        }
        const parsed = parse(stdout);
        resolve(parsed);
    });
});
